from __future__ import absolute_import, unicode_literals

from draftjs_exporter.dom import DOM


class Image():
    def render(self, props):
        data = props.get('data', {})

        return DOM.create_element('img', {
            'src': data.get('src'),
            'width': data.get('width'),
            'height': data.get('height'),
            'alt': data.get('alt'),
        })
