# Prints the emoji along with the corresponding hex value
def get_emoji_vals():
	for emoji in emojis:
		print((emoji, map(lambda uni: hex(ord(uni)), emoji)))

# Helps you distract how painstakingly slow your code is, with a little help from emoji
# Takes in frames for text and can cycle through those as well along with
# the emoji
def load_moji(func, args=[], kwargs={}, frames=[], fixed_width=True, text_color="green", text_attrs=["reverse", "bold"], width=10, delay=0.1):
	output = queue.Queue()

	def func_wrapper():
		if kwargs:
			result = func(*args, **kwargs)
		else:
			result = func(*args)
		if result:
			output.put(result)
	# We start running our function thread before we even start the animation.
	t = threading.Thread(target=func_wrapper)
	t.start()
	if fixed_width:
		# Finding the widest frame
		max_frame_length = len(max(frames, key=len))
		# Padding all of the frames to the same width
		frames = map(lambda frame: frame + ' ' *
					 (max_frame_length - len(frame)), frames)
	# Characters that are longer than 2 bytes get kind of glitchy so we filter
	# them out
	valid_emoji = filter(lambda emoji: len(emoji) < 3, emojis)
	# Adding text effects and creating cycler
	frames = cycle(map(lambda text: colored(' ' + text + ' ',
											color=text_color, attrs=text_attrs), frames))
	with hide_cursor():
		# Keep animation up until the thread terminates
		while t.is_alive():
			emoji = '  '.join(sample(valid_emoji, width))
			subprocess.call(["echo", frames.next(), "\n", emoji])
			sleep(delay)
			# Faster, less glitchy method of clearing than carriage returns,
			# bspace chars, etc.
			subprocess.call(["tput", "clear"])
	output = map(lambda item: output.get(item), range(0, output.qsize()))
	if output:
		return output


def random(count=1, byte_limit=None):
	valid_emoji = emojis
	if byte_limit:
		valid_emoji = filter(lambda emoji: len(emoji) < byte_limit, emojis)
	chosen = sample(valid_emoji, count)
	if len(chosen) == 1:
		chosen = chosen[0]
	return chosen
